class SideBar extends Component {
  state = {
    isBoxVisible:false
  };

  togglebox = () => {
    this.setState(prevState => ({ isBoxVisible: !prevState.isBoxVisible }));
  };

  render(){
    const { isBoxVisible } = this.state;
  return(
      <div>

            <div>
            <div className='side_ext_wrap ${isBoxVisible ? "" : "side_trans"'>

              <div className='side_ext'>


              </div>

            </div>
              <div className='side-bar-wrap' >

                <div className='bar'>
                  <Button onClick={this.togglebox} variant="transparent">
                    HOW TO FIND US
