import React from 'react';
import {
  BrowserRouter as Router,
  Route,
  NavLink
} from "react-router-dom";
import About from '../pages/aboutPage';
import Navbar from 'react-bootstrap/Navbar';
import AccBtn from '../pages/pageElements/accComp';

class Header extends React.Component{

  componentDidMount() {
    window.addEventListener("scroll", this.resizeHeaderOnScroll);
  }
  resizeHeaderOnScroll() {
    const distanceScrolled = window.pageYlimit || document.documentElement.scrollTop;
    const limit = 200;
    const header = document.getElementById("header");

    if (distanceScrolled > limit) {
      header.classList.add("shrink");
    } else {
      header.classList.remove("shrink");
    }
  }
  render(){
  return (
    <header id="header">

        <nav>
          <ul>
            <li>
              <NavLink className="nonActive" to="/" activeClassName="active">
              HOME
              </NavLink>
            </li>
            <li>
              <NavLink to="/About" activeClassName="active">
              ABOUT
              </NavLink>
            </li>
            <li>
              <NavLink to="/Policies" activeClassName="active">
              POLICIES
              </NavLink>
            </li>
            <li>
              <NavLink to="/Appointments" activeClassName="active">
              APPOINTMENTS
              </NavLink>
            </li>
          </ul>
        </nav>

        <div className="logo">
          <a href="/">IDEAL PAWN & JEWELRY</a>
        </div>

        <div>

          <AccBtn />
        </div>



    </header>
  );
}
}

export default Header;
